# Function to simulate typing effect
simulate_typing() {
    local text="$1"
    local delay="${2:-0.03}"
    for (( i=0; i<${#text}; i++ )); do
        printf "%c" "${text:$i:1}"
        sleep "$delay"
    done
    echo
}

